mapper.select:
select member_id,
member_name, phone_number, birthday,
gender,
member_level,
reg_date
from members where 1=1

	and (

		gender = #{gender}

	)


	and (

		member_level = #{level}

	)


	and member_id LIKE CONCAT('%', #{member_id}, '%')


	and member_name LIKE CONCAT('%', #{member_name}, '%')


	and
	reg_date > #{birthday_start}


	and reg_date < #{birthday_end}


	and reg_date < #{reg_date_start}


	and reg_date > #{reg_date_end}

order by reg_date desc
limit
#{startRowIndex},
#{viewPostCntPerPage}
