mapper.update:
update cart_items set product_count = #{quantity} 
where product_no = #{productNo} 
and cart_no = 
(select cart_no from carts
 where member_id = #{memberId})

update cart_items set product_count = product_count + #{quantity} 
where product_no = #{productNo} 
and cart_no = 
#{cartNo}

mapper.select:
select p.product_no, p.product_name, p.product_dc_type, p.dc_rate, p.product_price, i.image_main_url 
from product_images i inner join products p on p.product_no = i.product_no 
where i.image_sub_url = "M" and p.product_no = #{productNo}
