mapper.select:
select
inquiry_no, member_id, inquiry_title, inquiry_reg_date, inquiry_status
from inquiries
where member_id = #{member_id}
order by inquiry_no desc
limit #{pi.startRowIndex}, #{pi.viewDataCntPerPage}

mapper.update:
update inquiries set
product_no = #{product_no},
inquiry_title = #{inquiry_title}, inquiry_content = #{inquiry_content}, inquiry_type = #{inquiry_type} where inquiry_no = #{inquiry_no}
